import React, { useState } from 'react'

import {
  useReactTable,
  getCoreRowModel,
  getExpandedRowModel,
  ColumnDef,
  flexRender,
  RowData
} from '@tanstack/react-table'
import { col, transformData } from '../Spreadsheet/data'

declare module '@tanstack/react-table' {
  interface TableMeta<TData extends RowData> {
    updateData: (rowIndex: number, columnId: string, value: unknown) => void
  }
}

const EditableInput = ({ table, column, row, getValue }: any) => {
  const initialValue = getValue()
  const [value, setValue] = React.useState(initialValue)

  const onBlur = () => {
    table.options.meta?.updateData(row, column, value)
  }

  React.useEffect(() => {
    setValue(initialValue)
  }, [initialValue])

  return (
    <input
      value={value as string}
      onChange={e => setValue(e.target.value)}
      onBlur={onBlur}
      // onClick={() => console.log(row, column)}
      readOnly={checkIfDisabled(row, column)}
      style={{
        border: "none",
        borderRadius: 5,
        padding: "2px",
        color: "#333",
        backgroundColor: "#E5E4E2",
        outline: "none",
        textAlign: "center"
      }}
    />
  )
}

const checkIfDisabled = (row: any, column: any) => !Number(row.original[column.id] || 1)

const defaultColumn: Partial<ColumnDef<any>> = {
  cell: ({ 
    getValue,
    row,
    column,
    table
  }) => <EditableInput table={table} column={column} row={row} getValue={getValue} />,
}

export function useTable(data:any) {
  const [tabData, setTabData] = useState([])

  const newData = transformData(data.data, data)
  const tableData = newData.reduce((acc:any, val:any) => {
    Object.keys(val).forEach(e => {
        acc.push(val[e])
    })
    return acc
  }, [])

  setTabData(tabData)

  const columns = React.useMemo<ColumnDef<any>[]>(
    () => col(data),
    [data]
  )

  const table = useReactTable({
    data: tabData,
    columns,
    defaultColumn,
    getSubRows: (row: any) => row.subRows,
    getCoreRowModel: getCoreRowModel(),
    getExpandedRowModel: getExpandedRowModel(),
    meta: {
      updateData: (row, column, value) => {
        console.log(data)
      },
    },
    debugTable: true,
  })
  return { table, flexRender }
}
