import React, { useEffect, useMemo, useState } from "react";
import {
  useReactTable,
  getCoreRowModel,
  getExpandedRowModel,
  ColumnDef,
  flexRender,
} from "@tanstack/react-table";
import { useCallback } from "@types/react";

const EditableInput = ({ table, column, row, getValue }) => {
  const initialValue = getValue();
  const [value, setValue] = useState(initialValue);

  const disabled = !Number(row.original[column.id] || 1);

  useEffect(() => setValue(initialValue), [initialValue]);
  const onBlur = () => table.options.meta?.updateData(row, column, value);

  return (
    <input
      value={value}
      onChange={(e) => setValue(e.target.value)}
      onBlur={onBlur}
      readOnly={disabled}
      disabled={disabled}
      style={{
        border: "none",
        borderRadius: 5,
        padding: "2px",
        color: "#333",
        fontWeight: disabled ? 900 : 500,
        outline: "none",
        textAlign: "center",
        backgroundColor: disabled ? "lightgray" : "white",
      }}
    />
  );
};

const defaultColumn = {
  cell: ({ getValue, row, column, table }) => (
    <EditableInput
      table={table}
      column={column}
      row={row}
      getValue={getValue}
    />
  ),
};

export function useSpreadsheetTable(
  tableData,
  columns,
  data,
  setData,
  setSetter
) {
  const col = useMemo(() => columns, [columns]);

  const table = useReactTable({
    data: tableData,
    columns: col,
    defaultColumn,
    getSubRows: (rows) => rows.subRows,
    getCoreRowModel: getCoreRowModel(),
    getExpandedRowModel: getExpandedRowModel(),
    meta: {
      updateData: (row, column, value) => {
        setData((old) => {
          old.data[row.original.index][column.id] = value;
          return old;
        });
        setSetter((old) => !old);
      },
    },
    debugTable: true,
  });

  return { table, flexRender };
}
