import { useState, useEffect, SetStateAction } from 'react';
import { col, transformData } from '../Spreadsheet/data';

const useAPICall = (url:any, data: any, setData: { (value: SetStateAction<{}>): void; (arg0: any): void; }) => {


  const fetchData = async () => {
    console.log("===================api===========")
    try {
      const response = await fetch(url);

      const jsonData = await response.json();
      if (jsonData) {
        setData(jsonData);
      }

    } catch (error:any) {
      console.log("==========eorroo", error)
    }
  }

  useEffect(() => {
    if (!Object.keys(data).length) fetchData();
  }, [url, data]);

  return { data, setData };
};

export default useAPICall;
