      expense: { item: "expense", "jan-2023": 5, "feb-2023": 21, "mar-2023": 35, "total": "=(((B10)+(B11))+(B12))+(B13)" },
      loss: { item: "loss", "jan-2023": 50, "feb-2023": 20, "mar-2023": 47, "total": "=(((B10)+(B11))+(B12))+(B13)" },
      salary: { item: "salary", "jan-2023": 511, "feb-2023": 23, "mar-2023": 49, "total": "=(((B10)+(B11))+(B12))+(B13)" },
      income: { item: "income", "jan-2023": 59, "feb-2023": 25, "mar-2023": 45, "total": "=(((B10)+(B11))+(B12))+(B13)" },
      fixedDeposit: { item: "fixedDeposit", "jan-2023": 59, "feb-2023": 25, "mar-2023": 45, "total": "=(((B10)+(B11))+(B12))+(B13)" }
    },
    root: {
      expense: {
        loss: {}
      },
      salary: {
        income: {
            fixedDeposit: {
                expense:{
                    loss:{}
                }
            }
        } ,

    }
    }
  };

export const col = (data) => {
  data?.headers?.unshift("item")
  return data?.headers?.map(e => {
    return {
        accessorFn: row => {
          console.log("--------row", row);
        },
        accessorKey: e,
        ...(e==="item" && {cell: ({ row, getValue }) => {
            return (
            <div
              style={{
                paddingLeft: `${row.depth * 2}rem`,
              }}
            >
              <>
                {row.getCanExpand() ? (
                  <button
                    {...{
                      onClick: row.getToggleExpandedHandler(),
                      style: { cursor: 'pointer' },
                    }}
                  >
                    {row.getIsExpanded() ? '^' : '>'}
                  </button>
                ) : (
                  ''
                )}{' '}
                {getValue()}
              </>
            </div>
          )}}),
        header: () => e.toUpperCase(),
        footer: (props) => props.column.id,
    }
  })
}

  export function transformData(data, jsonData) {
    const output = [];
    for (const key in jsonData.root) {
      const item = jsonData.data[key];
      const subRows = transformSubRows(data, jsonData.root[key]);

      output.push({
        [key]: { ...item, subRows }
      });
    }
    return output;
  }

  function transformSubRows(data, subRowsData) {
    const subRows = [];  
      for (const key in subRowsData) {
        const item = data[key];
        const subRowData = item ? transformSubRows(data, subRowsData[key]) : undefined;
        subRows.push(
           { ...item, subRows: subRowData.length ? subRowData :undefined }
        );
      }
    return subRows;
  }

