import React from 'react'

import {
  useReactTable,
  getCoreRowModel,
  getExpandedRowModel,
  ColumnDef,
  flexRender,
  RowData
} from '@tanstack/react-table'
// import { makeData, Person } from './makeData'
import { col } from '../Spreadsheet/data'

declare module '@tanstack/react-table' {
  interface TableMeta<TData extends RowData> {
    updateData: (rowIndex: number, columnId: string, value: unknown) => void
  }
}

const EditableInput = ({ table, id, index, getValue }: any) => {
  const initialValue = getValue()
  const [value, setValue] = React.useState(initialValue)

  const onBlur = () => {
    table.options.meta?.updateData(index, id, value)
  }
  console.log(table)
  // console.log('CCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCCC',getValue(), id, index, table.getRow(index));

  React.useEffect(() => {
    setValue(initialValue)
  }, [initialValue])

  return (
    <input
      value={value as string}
      onChange={e => setValue(e.target.value)}
      onBlur={onBlur}
      style={{
        border: "none",
        borderRadius: 5,
        padding: "2px",
        color: "#333",
        backgroundColor: "#E5E4E2",
        outline: "none",
        textAlign: "center"
      }}
    />
  )
}

const defaultColumn: Partial<ColumnDef<any>> = {
  cell: ({ 
    getValue,
    row: { index },
    column: { id },
    table
  }) => <EditableInput table={table} id={id} index={index} getValue={getValue} />,
}

export function useTable(x:any, data:any) {
  const columns = React.useMemo<ColumnDef<any>[]>(
    () => x,
    [x]
  )

  const table = useReactTable({
    data,
    columns,
    defaultColumn,
    getSubRows: (row: any) => row.subRows,
    getCoreRowModel: getCoreRowModel(),
    getExpandedRowModel: getExpandedRowModel(),
    meta: {
      updateData: (rowIndex: any, columnId: any, value: any) => {
      },
    },
    debugTable: true,
  })
  return { table, flexRender }
}
