import React, { useEffect, useMemo, useState } from "react";
import {
  useReactTable,
  getCoreRowModel,
  getExpandedRowModel,
  ColumnDef,
  flexRender,
} from "@tanstack/react-table";

const EditableInput = ({ table, column, row, getValue }: any) => {
  const initialValue = getValue();
  const [value, setValue] = useState(initialValue);

  const onBlur = () => {
    table.options.meta?.updateData(row, column, value);
  };

  useEffect(() => {
    setValue(initialValue);
  }, [initialValue]);

  return (
    <input
      value={value as string}
      onChange={(e) => setValue(e.target.value)}
      onBlur={onBlur}
      readOnly={checkIfDisabled(row, column)}
      style={{
        border: "none",
        borderRadius: 5,
        padding: "2px",
        color: "#333",
        fontWeight: checkIfDisabled(row, column) ? 900 : 500,
        backgroundColor: "#E5E4E2",
        outline: "none",
        textAlign: "center",
      }}
    />
  );
};

const checkIfDisabled = (row: any, column: any) =>
  !Number(row.original[column.id] || 1);

const defaultColumn: Partial<ColumnDef<any>> = {
  cell: ({ getValue, row, column, table }) => (
    <EditableInput
      table={table}
      column={column}
      row={row}
      getValue={getValue}
    />
  ),
};

export function useTable(
  tableData: any,
  columns: any,
  data: any,
  setData: any,
  setSetter: any
) {
  const col = useMemo(() => columns, [columns]);

  const table = useReactTable({
    data: tableData,
    columns: col,
    defaultColumn,
    getSubRows: (row: any) => row.subRows,
    getCoreRowModel: getCoreRowModel(),
    getExpandedRowModel: getExpandedRowModel(),
    meta: {
      updateData: (row: any, column: any, value: any) => {
        setData((old: any) => {
          old.data[row.original.index][column.id] = value;
          return old;
        });
        setSetter((old: any) => !old);
      },
    },
    debugTable: true,
  });

  return { table, flexRender };
}
