const computeCell = (row, column, rawRows, computedRows) => {
  computedRows[row] = {
    ...computedRows[row],
    [column]:
      eval(
        rawRows[row]?.[column]?.replace(
          /'(.+?)'&'(.+?)'/g,
          (_, r, c) => {
            console.log(r)
            // r = Object.keys(rawRows).find(row => rawRows[row].displayName === r);
            return computedRows?.[r]?.[c] ?? computeCell(r, c, rawRows, computedRows)
        })
      ) ?? 0,
  };
  return computedRows[row][column];
};

export const col = (data) => {
  const computedRows = {};
  // const rows = Object.keys(data.data).map(row => {
  //   const { index, ...rest } = data.data[row]
  //   return { ...rest }
  // })
  // console.log(data.data)
  return data?.headers?.map(e => {
    return {
        accessorFn: row => {
          if (e !== "item")
            return (computeCell(row.index, e, data.data, computedRows)).toFixed(2)
          return row[e];
        },
        accessorKey: e,
        ...(e==="item" && {cell: ({ row, getValue }) => {
            return (
            <div
              style={{
                paddingLeft: `${row.depth * 2}rem`,
              }}
            >
              <>
                {row.getCanExpand() ? (
                  <button
                    {...{
                      onClick: row.getToggleExpandedHandler(),
                      style: { cursor: 'pointer' },
                    }}
                  >
                    {row.getIsExpanded() ? '^' : '>'}
                  </button>
                ) : (
                  ''
                )}{' '}
                {getValue()}
              </>
            </div>
          )}}),
        header: () => e.toUpperCase(),
        footer: (props) => props.column.id,
    }
  })
}

