import { useState, useEffect } from 'react';
import { col, transformData } from '../Spreadsheet/data';

const useAPICall = (url:any) => {
  const [data, setData] = useState(null);
  const [x, setX] = useState(null);
  // const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {


      try {
        const response = await fetch(url);

        // console.log(response)
        const jsonData = await response.json();
        // console.log(jsonData)
        if (jsonData) {
          const a = col(jsonData);
          setX(a);
          const newData = transformData(jsonData.rows, jsonData)
          const d = newData.reduce((acc:any, val:any)=>{
            Object.keys(val).forEach(e=>{
                acc.push(val[e])
            })
            return acc
          }, [])
          setData(d);
        }
        // setLoading(false);
      } catch (error:any) {
        setError(error);
        // setLoading(false);
      }
    };

    fetchData();
  }, [url]);

  return { data, error, x };
};

export default useAPICall;
