import React, { HTMLProps } from 'react'

import './index.css'

import {
  Column,
  Table,
  ExpandedState,
  useReactTable,
  getCoreRowModel,
  getPaginationRowModel,
  getFilteredRowModel,
  getExpandedRowModel,
  ColumnDef,
  flexRender,
  RowData
} from '@tanstack/react-table'
import { makeData, Person } from './makeData'

declare module '@tanstack/react-table' {
  interface TableMeta<TData extends RowData> {
    updateData: (rowIndex: number, columnId: string, value: unknown) => void
  }
}


const A = ({ table, id, index, getValue }: any) => {
  const initialValue = getValue()
  // We need to keep and update the state of the cell normally
  const [value, setValue] = React.useState(initialValue)

  // When the input is blurred, we'll call our table meta's updateData function
  const onBlur = () => {
    table.options.meta?.updateData(index, id, value)
  }

  // If the initialValue is changed external, sync it up with our state
  React.useEffect(() => {
    setValue(initialValue)
  }, [initialValue])

  return (
    <input
      value={value as string}
      onChange={e => setValue(e.target.value)}
      onBlur={onBlur}
    />
  )
}

const DefaultColumn: Partial<ColumnDef<Person>> = {
  cell: ({ getValue, row: { index }, column: { id }, table }) => <A table={table} id={id} index={index} getValue={getValue} />,
}



export function SpreadSheetTable() {
  // const rerender = React.useReducer(() => ({}), {})[1]

  const columns = React.useMemo<ColumnDef<Person>[]>(
    () => [
      {
        accessorKey: 'data',
        header: "Data",
        cell: ({ row, getValue }: any) => {
          console.log("UUUUUUUUUUUUUUUUUUUUUUUUUUUUUUUUUUUUUUU",getValue())
          return (
          <div
            style={{
              paddingLeft: `${row.depth * 2}rem`,
            }}
          >
            <>
              {row.getCanExpand() ? (
                <button
                  {...{
                    onClick: row.getToggleExpandedHandler(),
                    style: { cursor: 'pointer' },
                  }}
                >
                  {row.getIsExpanded() ? 'ðŸ‘‡' : 'ðŸ‘‰'}
                </button>
              ) : (
                'ðŸ”µ'
              )}{' '}
              {getValue()}
            </>
          </div>
        )},
        footer: (props: any) => props.column.id,
      },

      {
        accessorKey: "age",
        header: () => "JAN",
        footer: (props: any) => props.column.id,
      },
      {
        accessorKey: "feb",
        header: () => "Feb",
        footer: (props: any) => props.column.id,
      },
      {
        accessorKey: "Mar",
        header: "March",
        footer: (props: any) => props.column.id,
      },
      {
        accessorKey: "apr",
        header: "April",
        footer: (props: any) => props.column.id,
      },
    ],
    []
  )

  const [data, setData] = React.useState(() => makeData(10, 5, 3))
  // const refreshData = () => setData(() => makeData(100, 5, 3))

  console.log(data)

  const [expanded, setExpanded] = React.useState<ExpandedState>({})

  const table = useReactTable({
    data,
    columns,
    defaultColumn: DefaultColumn,
    state: {
      expanded,
    },
    onExpandedChange: setExpanded,
    getSubRows: (row: any) => row.subRows,
    getCoreRowModel: getCoreRowModel(),
    // getPaginationRowModel: getPaginationRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    getExpandedRowModel: getExpandedRowModel(),
    meta: {
      updateData: (rowIndex: any, columnId: any, value: any) => {
        // Skip page index reset until after next rerender
        setData(old =>
          old.map((row, index) => {
            if (index === rowIndex) {
              return {
                ...old[rowIndex]!,
                [columnId]: value,
              }
            }
            return row
          })
        )
      },
    },
    debugTable: true,
  })

  return (
    <div className="p-2">
      <div className="h-2" />
      <table>
        <thead>
          {table.getHeaderGroups().map((headerGroup: any) => (
            <tr key={headerGroup.id}>
              {headerGroup.headers.map((header: any) => {
                return (
                  <th key={header.id} colSpan={header.colSpan}>
                    {header.isPlaceholder ? null : (
                      <div>
                        {flexRender(
                          header.column.columnDef.header,
                          header.getContext()
                        )}
                        {/* {header.column.getCanFilter() ? (
                          <div>
                            <Filter column={header.column} table={table} />
                          </div>
                        ) : null} */}
                      </div>
                    )}
                  </th>
                )
              })}
            </tr>
          ))}
        </thead>
        <tbody>
          {table.getRowModel().rows.map((row: any) => {
            return (
              <tr key={row.id}>
                {row.getVisibleCells().map((cell: any) => {
                  return (
                    <td key={cell.id}>
                      {flexRender(
                        cell.column.columnDef.cell,
                        cell.getContext()
                      )}
                    </td>
                  )
                })}
              </tr>
            )
          })}
        </tbody>
      </table>
      <div className="h-2" />
    </div>
  )
}

