import getComputedCell from "./getComputedCell";

export const computeCell = (row, column, rawRows, computedRows) => {
  computedRows[row] = {
    ...computedRows[row],
    [column]:
      eval(
        rawRows[row][column].replace(/'(.+?)'&'(.+?)'/g, (_, r, c) => {
          return (
            computedRows?.[r]?.[c] ?? computeCell(r, c, rawRows, computedRows)
          );
        })
      ) ?? 0,
  };

  return computedRows[row][column];
};

export const col = (data) => {
  const computedRows = {};

  return data?.headers?.map((e) => {
    return {
      accessorFn: (row) => {
        if (e !== "item")
          return getComputedCell(row.index, e, data.data, computedRows).toFixed(
            2
          );
        return row[e];
      },
      accessorKey: e,
      ...(e === "item" && {
        cell: ({ row, getValue }) => {
          return (
            <div
              style={{
                paddingLeft: `${row.depth * 2}rem`,
              }}
            >
              <>
                {row.getCanExpand() ? (
                  <button
                    {...{
                      onClick: row.getToggleExpandedHandler(),
                      style: { cursor: "pointer" },
                    }}
                  >
                    {row.getIsExpanded() ? "^" : ">"}
                  </button>
                ) : (
                  ""
                )}{" "}
                {getValue()}
              </>
            </div>
          );
        },
      }),
      header: () => e.toUpperCase(),
      footer: (props) => props.column.id,
    };
  });
};

export function transformData(data, jsonData) {
  const output = [];
  for (const key in jsonData.root) {
    const item = jsonData.data[key];
    const subRows = transformSubRows(data, jsonData.root[key]);

    output.push({
      [item.displayName]: { ...item, subRows },
    });
  }
  return output;
}

function transformSubRows(data, subRowsData) {
  const subRows = [];
  for (const key in subRowsData) {
    const item = data[key];
    const subRowData = item
      ? transformSubRows(data, subRowsData[key])
      : undefined;
    subRows.push({
      ...item,
      subRows: subRowData.length ? subRowData : undefined,
    });
  }
  return subRows;
}
