import React, { HTMLProps, useEffect, useState } from 'react'

import './index.css'
// import { Table } from './Table'
import { useTable } from '../customHook/useCreAteTable'
import useAPICall from '../customHook/useApiCall'
import { col, transformData } from './data'
import { ColumnDef } from '@tanstack/react-table'

export const SpreadsheetList = () => {
  const data = [{name: 'Spreadsheet'}, {name: 'Spreadsheet2'}]

  return (<div className="spreadsheet-list">
    {
      data.map((current) => {
        return (<div className="spreadsheet-list-item">
          <img className='spreadsheet-list-img' src={'../../spreadsheet-image.png'} />
          <h4>{current.name}</h4>
        </div>)
      })
    }
  </div>)
}



const SIDEBAR_CLASSES:any = {
  'sidebar-container': 'collapsed-sidebar',
  'collapsed-sidebar': 'sidebar-container'
};

const SpreadSheetTable = () => {

  const { data, setData } = useAPICall("http://localhost:4001/api/getData")
  const [ tableData, setTableData ] = useState(null)
  const [ columns, setColumns ] = useState(null);
  useEffect(() => {
    const newData = transformData(data?.data || {})
    setTableData(newData.reduce((acc:any, val:any) => {
      Object.keys(val).forEach(e => {
          acc.push(val[e])
      })
      return acc
    }, []))

    const col: any = React.useMemo(
      () => col(data),
      [data]
    )
    setColumns(col)
  }, [])

  const { table, flexRender } = useTable(data || {})
  const ref = React.useRef(null);

  const handleCollapseButton =(reff: any) => {
    if (reff === undefined) return;

    return reff.current.className = SIDEBAR_CLASSES[reff.current.className];
  }
  if  (!data) return <></>

  return (
    <div className="parent">
      <div className="h-2" />
      <div className="sidebar">
          <div className='sidebar-container'  ref={ref}>
            <img src={'../../kordis-logo.png'} />
            <SpreadsheetList />
          </div>
          <button
            className='collapse-button'
            onClick={() => handleCollapseButton(ref)}
          >
            <img className='sidebar-collapse-img' src={'../../collapse-button.jpg'} />
          </button>
        </div>
        <div className='container'>
            <div className="spreadsheet-text">Spreadsheet</div>
              <table className='table'>
                <thead>
                  {table.getHeaderGroups().map((headerGroup: any) => (
                    <tr key={headerGroup.id}>
                      {headerGroup.headers.map((header: any) => {
                        return (
                          <th key={header.id} colSpan={header.colSpan}>
                            {header.isPlaceholder ? null : (
                              <div>
                                {flexRender(
                                  header.column.columnDef.header,
                                  header.getContext()
                                )}
                              </div>
                            )}
                          </th>
                        )
                      })}
                    </tr>
                  ))}
                </thead>
                <tbody>
                  {table.getRowModel().rows.map((row: any) => {
                    return (
                      <tr key={row.id}>
                        {row.getVisibleCells().map((cell: any) => {
                          return (
                            <td key={cell.id}>
                              {flexRender(
                                cell.column.columnDef.cell,
                                cell.getContext()
                              )}
                            </td>
                          )
                        })}
                      </tr>
                    )
                  })}
                </tbody>
              </table>
          </div>
        </div>
  )

}
export default SpreadSheetTable
