import { useState, useEffect } from 'react';
import { col, transformData } from '../Spreadsheet/data';
import { log } from 'console';

const useAPICall = (url:any) => {
  const [data, setData] = useState(null);
  const [x, setX] = useState(null);
  // const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      console.log();

      try {
        const response = await fetch(url);

        console.log(response)
        const jsonData = await response.json();
        console.log(jsonData)
        if (jsonData) {
          console.log(col(jsonData));

          setX(col(jsonData))
          const newData = transformData(jsonData)
          console.log("IIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIIII",newData);

          const d = newData.reduce((acc:any, val:any)=>{
            Object.keys(val).forEach(e=>{
                acc.push(val[e])
            })
            return acc
          }, [])
          setData(d);
        }
        // setLoading(false);
      } catch (error:any) {
        setError(error);
        // setLoading(false);
      }
    };

    fetchData();
  }, [url]);

  return { data, error, x };
};

export default useAPICall;
