import React, { useCallback } from "react";
import SpreadsheetList from "./SpreadsheetList";
import "../SpreadSheet/index.css";

const SIDEBAR_CLASSES = {
  "sidebar-container": "collapsed-sidebar",
  "collapsed-sidebar": "sidebar-container",
};

const Sidebar = () => {
  const sidebarRef = React.useRef(null);

  const handleCollapseButton = useCallback((ref) => {
    if (ref === undefined) return;
    ref.current.className = SIDEBAR_CLASSES[ref.current.className];
  }, []);

  return (
    <div className="sidebar">
      <div>
        <button
          className="collapse-button"
          onClick={() => handleCollapseButton(sidebarRef)}
        >
          <img src={"../../kordis-logo.png"} />
        </button>
      </div>

      <div className="sidebar-container" ref={sidebarRef}>
        <SpreadsheetList />
      </div>
    </div>
  );
};

export default Sidebar;
