import React, { useEffect, useState } from "react";
import getTransformedData from "./getTransformedData";
import { useSpreadsheetTable } from "./useSpreadsheetTable";
import getColumns from "./getColumns";
import "./index.css";

const SpreadSheetTable = ({ value }) => {
  const [data, setData] = useState(value);
  const [tableData, setTableData] = useState([]);
  const [columns, setColumns] = useState([]);
  const [setter, setSetter] = useState(false);

  useEffect(() => {
    if (Object.keys(data).length) {
      const newData = getTransformedData(data.data, data);
      setTableData(
        newData.reduce((acc, val) => {
          Object.keys(val).forEach((e) => {
            acc.push(val[e]);
          });
          return acc;
        }, [])
      );

      setColumns(getColumns(data));
    }
  }, [setter, data, setTableData, setColumns]);

  const { table, flexRender } = useSpreadsheetTable(
    tableData,
    columns,
    data,
    setData,
    setSetter
  );

  if (!tableData) return <></>;

  return (
    <div className="parent">
      <div className="h-2" />
      <Sidebar />
      <div className="container">
        <div className="spreadsheet-text">Spreadsheet</div>
        <table className="table">
          <thead>
            {table.getHeaderGroups().map((headerGroup) => (
              <tr key={headerGroup.id}>
                {headerGroup.headers.map((header) => {
                  return (
                    <th key={header.id} colSpan={header.colSpan}>
                      {header.isPlaceholder ? null : (
                        <div>
                          {flexRender(
                            header.column.columnDef.header,
                            header.getContext()
                          )}
                        </div>
                      )}
                    </th>
                  );
                })}
              </tr>
            ))}
          </thead>

          <tbody>
            {table.getRowModel().rows.map((row) => {
              return (
                <tr key={row.id}>
                  {row.getVisibleCells().map((cell) => {
                    return (
                      <td key={cell.id}>
                        {flexRender(
                          cell.column.columnDef.cell,
                          cell.getContext()
                        )}
                      </td>
                    );
                  })}
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default SpreadSheetTable;
