export function getTransformedData(rows, data) {
  const output = [];

  const rootKeys = Object.keys(data.root);

  return rootKeys.map((key) => {
    const item = data.data[key];
    const subRows = transformSubRows(rows, data.root[key]);

    return {
      [item.displayName]: { ...item, subRows },
    };
  });
}

function transformSubRows(data, subRowsData) {
  const subRows = [];
  for (const key in subRowsData) {
    const item = data[key];
    const subRowData = item
      ? transformSubRows(data, subRowsData[key])
      : undefined;
    subRows.push({
      ...item,
      subRows: subRowData.length ? subRowData : undefined,
    });
  }
  return subRows;
}
